/**
 * Model for User JPA entity layout
 */
package com.iisc.csa.pods.projects.user.model;

import jakarta.persistence.*;
import lombok.Data;


/**
 * JPA Model class for User Entity.<br/><br/>
 *
 * The entity is named UserTable as there seems to another Table within Spring framework of the same name
 * and seems to be creating conflicts on initialization.
 */
@Entity
@Data
@Table(name = "users")
public class UserTable {
    /**
     * Primary key - ID field for User entity.<br/><br/>
     *
     * Values will be autogenerated for each user registration.
     */
    @Id
        @GeneratedValue(strategy = GenerationType.SEQUENCE, generator="useridgen")
        @SequenceGenerator(name="useridgen", initialValue = 1, allocationSize = 1)
    Integer id;

    /**
     * Name of user
     */
    @Column(name="name")
    private String name;

    /**
     * Email address of user.
     *
     * TODO/WARN: Malformed input validation is not done for now.
     */
    @Column(name="email", unique=true)
    private String email;

    public UserTable(){}
    public UserTable(String name_, String email_ ) {
        this.name = name_;
        this.email = email_;
    }

    @Override
    public String toString() {
        return this.name+","+this.email;
    }
}
